class FileList(views.APIView):
    def get(self, request, *args, **kwargs):
        page = kwargs.get('page', 1)
        step = settings.FILE_STEP
        rang = (page * step - step, page * step)
        files = list(mongo_db.file.find().sort('published_date', -1).skip(rang[0]).limit(rang[1]))
        page_count = get_page_count(len(files), step)
        return ResponseMongo({'files': files, 'page_count': page_count})

    def post(self, request, *args, **kwargs):
        s = FileMongoSerializer(data=request.data, request=request)
        if s.is_valid():
            s.save()
        else:
            return Response(s.errors)


class FileDetail(views.APIView):
    def get(self, request, *args, **kwargs):
        data = mongo_db.file.find_one({'_id': ObjectId(kwargs['id'])})
        return ResponseMongo(data)

    def put(self, request, *args, **kwargs):
        s = FileMongoSerializer(_id=kwargs['id'], data=request.data, request=request, partial=True)
        if s.is_valid():
            s.save()
        else:
            return Response(s.errors)


divar_verification_code = {'code': ''}
class SmsCode(views.APIView):
    def get(self, request, *args, **kwargs):
        return Response(divar_verification_code)

    def post(self, request, *args, **kwargs):
        divar_verification_code['code'] = request.data['code']
        return Response({'status': f"{divar_verification_code['code']} successfully set."})


class LoginDivar(views.APIView):
    # send verification code and wait to submit it in SmsCode.post and receive it and login to save credentials
    def get(self, request, *args, **kwargs):
        return ResponseMongo({'phone': 'post your phone to log in divar.ir, like: 9102337665'})

    def post(self, request, *args, **kwargs):
        # 1 set location 'tehran' to cookie  2 after send SMS code, logged in. both saves in the current chrome profile
        # without login, some properties' phone not shown
        phone_number = '9102337664'#request.data['phone']
        driver = setup_driver()

        try:
            url = "https://divar.ir/s/tehran/buy-apartment"
            driver.get(url)  # Load the web page
            time.sleep(2)

            # add location 'tehran' to the site
            cookies = [{"name": "city", "value": "tehran", "domain": ".divar.ir"},
                       {"name": "multi-city", "value": "tehran%7C", "domain": ".divar.ir"}]
            for cookie in cookies:
                driver.add_cookie(cookie)
            driver.refresh()

            # 'دیوار من' button
            navbar_button = WebDriverWait(driver, 10).until(EC.visibility_of_element_located((By.XPATH,"//button[@class='kt-button kt-button--inlined kt-nav-button nav-bar__btn kt-nav-button--small']")))
            navbar_button.click()

            # first get parent element to find sub elements without errors
            dropdown_box = WebDriverWait(driver, 10).until(EC.presence_of_element_located((By.XPATH, "//div[@class='kt-dropdown-menu__menu kt-dropdown-menu__menu--no-smooth kt-dropdown-menu__menu--open']")))

            # in dropdown_box, several buttons can contain same attrs
            buttons = dropdown_box.find_elements(By.CLASS_NAME, "kt-fullwidth-link.kt-fullwidth-link--small.navbar-my-divar__button-item")
            for element in buttons:
                if element.find_element(By.TAG_NAME, "p").text.strip() == "ورود":  # get only button 'ورود'
                    # click on 'vorod' button
                    element.click()
                    break
            else:     # if not find 'vorod' button (loop complete to the end)
                return Response({'status': 'user has logged in before'})

            # first get parent_box to find sub elements without errors
            parent_box = WebDriverWait(driver, 10).until(EC.presence_of_element_located((By.XPATH, "//section[@class='kt-new-modal kt-new-modal--stickyfooter']")))

            # enter phone number in the input to send verification
            input_field = parent_box.find_element(By.XPATH, "//input[@placeholder='شمارهٔ موبایل']")
            input_field.send_keys(phone_number)

            # wait until send verification code
            status = ''
            global divar_verification_code
            for i in range(20):
                if divar_verification_code['code']:
                    input_field = WebDriverWait(driver, 10).until(EC.presence_of_element_located((By.CSS_SELECTOR, "input[placeholder='کد تأیید ۶ رقمی']")))
                    input_field.send_keys(divar_verification_code['code'])
                    status = 'ok'
                    divar_verification_code['code'] = ''     # this global variable must reset
                    break
                else:
                    time.sleep(4)
            time.sleep(8)
            if status:
                return Response({'status': 'successfully logged in'})
            else:
                return Response({'status': "couldn't log into divar.ir"})
        except Exception as e:
            raise e
        finally:
            driver.quit()


class FileCrawl(views.APIView):
    def get(self, request, *args, **kwargs):
        return ResponseMongo()

    def post(self, request, *args, **kwargs):
        location_to_search = 'کیانشهر'  # request.data['location_to_search']  # like 'کیانشهر'
        files, errors = crawl_files(location_to_search, 2)
        unique_titles, unique_files = [], []
        for file in files:    # field unique validation only done when save file singular (so we have to validate here)
            if file['title'] not in unique_titles:
                cleaned_file = {key: value for key, value in file.items() if value is not None}
                unique_titles.append(cleaned_file['title'])
                unique_files.append(cleaned_file)
        if unique_titles:
            s = FileMongoSerializer(data=unique_files, request=request, many=True)
            if s.is_valid():
                files = s.save()
                return ResponseMongo({'files_saved': files, 'files_failed': errors})
            else:
                return ResponseMongo(s.errors)
        else:
            return Response({'files_failed': errors})
